const rendSec3 = (obj , tag) => {
    obj.forEach(item => {
        const elicon = document.createElement('div')
        const eltitle = document.createElement('div')
        const eldes = document.createElement('div')
        const ellink = document.createElement('button')
        const el = document.createElement('div')
        
        elicon.innerHTML = item.icon
        eltitle.innerHTML = item.title
        eldes.innerHTML = item.des
        ellink.innerHTML = item.link

        eltitle.className = 'sec-3-title'
        eldes.className = 'sec-3-des'

        el.append(elicon)
        el.append(eltitle)
        el.append(eldes)
        el.append(ellink)

        tag.appendChild(el)
    })
}
